package de.tum.cs.i1.pse;public class InventorySystem {	private int totalStock;	private Warehouse warehouse = new WarehouseImpl();	public void addToWarehouse(String item, int amount) {		totalStock += amount;		warehouse.add(item, amount);	}		public int getTotalStock() {		return totalStock;	}		public boolean processOrder(Order order) {		order.fill(warehouse);		return order.isFilled();	}}